import React, { useEffect, useState } from 'react';
import { fetchSalesData } from './shopify';

function App() {
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    const getData = async () => {
      try {
        const salesData = await fetchSalesData();
        console.log(salesData);
        setOrders(salesData);
      } catch (error) {
        console.error("Failed to fetch sales data:", error);
      }
    };

    getData();
  }, []);

  return (
    <div className="App">
      <h1>Shopify Orders</h1>
      <ul>
        {orders.map((order) => (
          <li key={order.node.id}>
            <strong>Order Name:</strong> {order.node.name} <br />
            <strong>Customer:</strong> {order.node.customer ? `${order.node.customer.firstName} ${order.node.customer.lastName}` : "Not available"} <br />
            <strong>Total Price:</strong> {order.node.totalPriceSet ? `${order.node.totalPriceSet.presentmentMoney.amount} ${order.node.totalPriceSet.presentmentMoney.currencyCode}` : "Price not available"} <br />
            <strong>Items:</strong>
            <ul>
              {order.node.lineItems.edges.map(item => (
                <li key={item.node.title}>
                  {item.node.title} - Quantity: {item.node.quantity} - Price: {item.node.priceSet.presentmentMoney.amount} {item.node.priceSet.presentmentMoney.currencyCode}
                </li>
              ))}
            </ul>
            <strong>Source:</strong> {order.node.sourceName}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
