import React, { useEffect, useState } from 'react';
import { fetchOrders } from './shopify';

function App() {
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    const getData = async () => {
      try {
        const ordersData = await fetchOrders();
        setOrders(ordersData);
      } catch (error) {
        console.error("Failed to fetch orders:", error);
      }
    };

    getData();
  }, []);

  return (
    <div className="App">
      <h1>Shopify Orders</h1>
      <ul>
        {orders.map((order) => (
          <li key={order.node.id}>
            <strong>Order Name:</strong> {order.node.name} <br />
            <strong>Total Price:</strong> {order.node.totalPriceSet ? `${order.node.totalPriceSet.presentmentMoney.amount} ${order.node.totalPriceSet.presentmentMoney.currencyCode}` : "Price not available"}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
