import axios from 'axios';

export const fetchSalesData = async () => {
  try {
    const response = await axios.post('http://localhost:5001/fetch-sales-data');
    return response.data;
  } catch (error) {
    console.error("Error fetching data:", error.message);
    throw error;
  }
};
