import React, { useEffect, useState } from 'react';
import { fetchSalesData } from './shopify';

function App() {
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    const getData = async () => {
      try {
        const salesData = await fetchSalesData();
        setOrders(salesData);
      } catch (error) {
        console.error("Failed to fetch sales data:", error);
      }
    };

    getData();
  }, []);

  return (
    <div className="App">
      <h1>Shopify Orders</h1>
      <ul>
        {orders.map((order) => (
          <li key={order.node.id}>
            {order.node.name} - {order.node.totalPriceSet.presentmentMoney.amount} {order.node.totalPriceSet.presentmentMoney.currencyCode}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
