import axios from 'axios';

export const fetchOrders = async () => {
  try {
    const response = await axios.get('http://localhost:5001/fetch-orders');
    return response.data;
  } catch (error) {
    console.error("Error fetching orders:", error.message);
    throw error;
  }
};
