import React, { useEffect, useState } from 'react';
import { fetchOrders, fetchCustomers } from './shopify';

function App() {
  const [orders, setOrders] = useState([]);
  const [customers, setCustomers] = useState([]);

  useEffect(() => {
    const getData = async () => {
      try {
        const ordersData = await fetchOrders();
        const customersData = await fetchCustomers();
        setOrders(ordersData);
        setCustomers(customersData);
      } catch (error) {
        console.error("Failed to fetch data:", error);
      }
    };

    getData();
  }, []);

  return (
    <div className="App">
      <h1>Shopify Orders</h1>
      <ul>
        {orders.map((order) => (
          <li key={order.node.id}>
            <strong>Order Name:</strong> {order.node.name} <br />
            <strong>Customer:</strong> {order.node.customer ? `${order.node.customer.firstName} ${order.node.customer.lastName}` : "Not available"} <br />
            <strong>Total Price:</strong> {order.node.totalPriceSet ? `${order.node.totalPriceSet.presentmentMoney.amount} ${order.node.totalPriceSet.presentmentMoney.currencyCode}` : "Price not available"} <br />
            <strong>Items:</strong>
            <ul>
              {order.node.lineItems.edges.map(item => (
                <li key={item.node.title}>
                  {item.node.title} - Quantity: {item.node.quantity} - Price: {item.node.priceSet.presentmentMoney.amount} {item.node.priceSet.presentmentMoney.currencyCode}
                </li>
              ))}
            </ul>
            <strong>Source:</strong> {order.node.sourceName}
          </li>
        ))}
      </ul>

      <h1>Shopify Customers</h1>
      <ul>
        {customers.map((customer) => (
          <li key={customer.node.id}>
            <strong>Name:</strong> {customer.node.firstName} {customer.node.lastName} <br />
            <strong>Email:</strong> {customer.node.email} <br />
            <strong>Orders Count:</strong> {customer.node.ordersCount}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
